package com.latifapp.latif.uiimport android.content.Intentimport androidx.appcompat.app.AppCompatActivityimport android.os.Bundleimport android.os.Handlerimport android.os.Looperimport android.view.WindowManagerimport com.fujiyuu75.sequent.Animationimport com.fujiyuu75.sequent.Directionimport com.fujiyuu75.sequent.Sequentimport com.latifapp.latif.Rimport com.latifapp.latif.ui.auth.login.LoginActivityclass SplashActivity : AppCompatActivity() {    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        setContentView(R.layout.activity_splash)        window.setFlags(            WindowManager.LayoutParams.FLAG_LAYOUT_NO_LIMITS,            WindowManager.LayoutParams.FLAG_LAYOUT_NO_LIMITS        )        Sequent            .origin(findViewById(R.id.logo))            .duration(1500) // option.            .flow(Direction.RANDOM) // option. Flow of animations in (FORWARD/BACKWARD/RANDOM).            .anim(this, Animation.FADE_IN)            .start()        intentToNextPage()            // intent    }    private fun intentToNextPage() {        Handler(Looper.getMainLooper()).postDelayed({            /* Create an Intent that will start the Menu-Activity. */            startActivity(Intent(this@SplashActivity,LoginActivity::class.java))            finish()        }, 3000)    }}